mapper.select:
SELECT 

FROM attitude a

WHERE a.id = #{id}

SELECT 

FROM attitude a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.model_id = #{modelId}


		AND a.model_name = #{modelName}


		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM attitude a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE attitude SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
