mapper.select:
SELECT 

FROM cms_comment a

WHERE a.id = #{id}

SELECT 

FROM cms_comment a


	a.del_flag = #{delFlag}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			CONCAT('%', #{title}, '%')


		AND a.content_id = #{contentId}


		AND a.content_model = #{contentModel}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT a.id AS "id", a.content_id AS "contentId", CASE WHEN a.content_model is NULL THEN "" ELSE a.content_model END AS "contentModel", CASE WHEN a.content is NULL THEN "" ELSE a.content END AS "content", CASE WHEN u.name is NULL THEN "" ELSE u.name END AS "name", CASE WHEN u.photo is NULL THEN "" ELSE u.photo END AS "photo", CASE WHEN u.photo is NULL THEN "" ELSE date_format(a.create_date,'%Y-%m-%d %T') END AS "createDate", SUM(CASE WHEN att.`status` = '1' THEN 1 ELSE 0 END) AS "goodNum", SUM(CASE WHEN att.`status` = '0' THEN 1 ELSE 0 END) AS "badNum" FROM cms_comment a LEFT JOIN attitude att ON a.id = att.model_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.content_id = #{contentId}
AND a.content_model = #{contentModel}
GROUP BY a.id
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT 

FROM cms_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cms_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
