mapper.select:
SELECT 

FROM focus a

WHERE a.id = #{id}

SELECT 

FROM focus a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM focus a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE focus SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
