mapper.select:
SELECT 

FROM message a

WHERE a.id = #{id}

SELECT 

FROM message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE message SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
