mapper.update:
UPDATE post SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.content AS "content", a.post_id AS "postId", a.main_id AS "postMainId", a.create_by AS "createById", u.name AS "createByName", u.photo AS "createByPhoto", date_format(a.create_date,"%Y-%m-%d %T") AS "createDate", a.remarks AS "remarks" FROM post a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.main_id = #{postMain.id}
AND a.post_id = #{postId}
AND a.post_id is NULL
ORDER BY a.create_date DESC
