mapper.select:
SELECT 

FROM team a

WHERE a.id = #{id}

SELECT
FROM team a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM team a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE team SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "teamId", a.photo AS "photo", a.name AS "name", a.person_num AS "personNum", a.group_id AS "groupId", c.name AS "groupName", a.position AS "position", a.position_x AS "positionX", a.position_y AS "positionY", a.create_by AS "createById", u.name AS "createByName", date_format(a.create_date,'%Y-%m-%d %T') AS "createDate", a.remarks AS "remarks", SUM(CASE WHEN f.create_by = #{currentUser.id} AND f.del_flag = #{DEL_FLAG_NORMAL} THEN 1 ELSE 0 END) AS "hasFocus", sum(CASE WHEN f.del_flag = #{DEL_FLAG_NORMAL} THEN 1 ELSE 0 END ) AS "focusNum" FROM team a
LEFT JOIN focus f ON f.model_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.group_id = #{group.id}
${sqlMap.addition}
GROUP BY a.id
ORDER BY ${page.orderBy} DESC
ORDER BY a.create_date DESC
