mapper.select:
SELECT

FROM reserve_project a

WHERE a.id = #{id}

SELECT
FROM reserve_project a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT a.id AS "projectId", a.name AS "projectName" FROM reserve_project a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM reserve_project a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE reserve_project SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
