mapper.select:
select SUM(total_sum)
from reserve_commodity_sell as s
WHERE  datediff(s.update_date,curdate())=0 and s.tenant_id = #{tenantId}

SELECT SUM(total_sum) AS "volume",date_format(a.update_date,'%Y-%m-%d') AS "updateDate" from reserve_commodity_sell as a WHERE a.tenant_id = #{tenantId} and
DATE_SUB(CURDATE(), INTERVAL 10 DAY) <= date(a.update_date)
GROUP BY date_format(a.update_date, '%Y-%m-%d');

SELECT t.id as "reserveCommodityType.id", t.name as "reserveCommodityType.name", sum(d.detail_sum) as "bill", sum(if(pay_type=1,d.detail_sum,0)) AS "storedCardBill", sum(if(pay_type=2,d.detail_sum,0)) AS "cashBill", sum(if(pay_type=3,d.detail_sum,0)) AS "bankCardBill", sum(if(pay_type=11,d.detail_sum,0)) AS "transferBill", sum(if(pay_type=4,d.detail_sum,0)) AS "weiXinBill", sum(if(pay_type=9,d.detail_sum,0)) AS "personalWeiXinBill", sum(if(pay_type=5,d.detail_sum,0)) AS "aliPayBill", sum(if(pay_type=10,d.detail_sum,0)) AS "personalAliPayBill", sum(if(pay_type=6,d.detail_sum,0)) AS "otherBill", sum(if(pay_type=7,d.detail_sum,0)) AS "dueBill" from reserve_commodity_sell_detail as d LEFT JOIN reserve_commodity_sell as s ON d.fk_reserve_commodity_sell_id = s.id LEFT JOIN reserve_commodity as c ON c.id = d.fk_reserve_commodity_id LEFT JOIN reserve_commodity_type as t ON c.commodity_type_id = t.id LEFT JOIN reserve_venue as v ON c.fk_reserve_venue_id = v.id
d.del_flag = '0' and d.tenant_id = '1'
and datediff(d.update_date,#{startDate})>=0 and datediff(#{endDate},d.update_date)>=0
and v.id=#{reserveVenue.id}
${sqlMap.dsf}
GROUP BY t.id

SELECT d.id AS "reserveVenue.id", d.name AS "reserveVenue.name", b.id AS "reserveCommodity.id", b.name AS "reserveCommodity.name", c.id AS "reserveCommodityType.id", c.name AS "reserveCommodityType.name", SUM(a.detail_sum) AS "bill", sum(if(pay_type=1,a.detail_sum,0)) AS "storedCardBill", sum(if(pay_type=2,a.detail_sum,0)) AS "cashBill", sum(if(pay_type=3,a.detail_sum,0)) AS "bankCardBill", sum(if(pay_type=4,a.detail_sum,0)) AS "weiXinBill", sum(if(pay_type=9,a.detail_sum,0)) AS "personalWeiXinBill", sum(if(pay_type=5,a.detail_sum,0)) AS "aliPayBill", sum(if(pay_type=10,a.detail_sum,0)) AS "personalAliPayBill", sum(if(pay_type=6,a.detail_sum,0)) AS "otherBill", sum(if(pay_type=7,a.detail_sum,0)) AS "dueBill", #{day} AS "day" from reserve_commodity_sell_detail as a, reserve_commodity as b,reserve_commodity_type as c,reserve_venue as d,reserve_commodity_sell as e where a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId} and a.fk_reserve_commodity_id=b.id and b.fk_reserve_venue_id=d.id and c.id=b.commodity_type_id and e.id=a.fk_reserve_commodity_sell_id
and d.id=#{reserveVenue.id}
and c.id=#{reserveCommodityType.id}
and b.id=#{reserveCommodity.id}
and Date(a.update_date )=Date(#{day})
GROUP BY b.id,c.id,d.id

SELECT d.id AS "reserveVenue.id", d.name AS "reserveVenue.name", b.id AS "reserveCommodity.id", b.name AS "reserveCommodity.name", c.id AS "reserveCommodityType.id", c.name AS "reserveCommodityType.name", SUM(a.detail_sum) AS "bill", sum(if(pay_type=1,a.detail_sum,0)) AS "storedCardBill", sum(if(pay_type=2,a.detail_sum,0)) AS "cashBill", sum(if(pay_type=3,a.detail_sum,0)) AS "bankCardBill", sum(if(pay_type=11,a.detail_sum,0)) AS "transferBill", sum(if(pay_type=4,a.detail_sum,0)) AS "weiXinBill", sum(if(pay_type=9,a.detail_sum,0)) AS "personalWeiXinBill", sum(if(pay_type=5,a.detail_sum,0)) AS "aliPayBill", sum(if(pay_type=10,a.detail_sum,0)) AS "personalAliPayBill", sum(if(pay_type=6,a.detail_sum,0)) AS "otherBill", sum(if(pay_type=7,a.detail_sum,0)) AS "dueBill", #{startDate} AS "startDate", #{endDate} AS "endDate" from reserve_commodity_sell_detail as a, reserve_commodity as b,reserve_commodity_type as c,reserve_venue as d,reserve_commodity_sell as e where a.fk_reserve_commodity_id=b.id and b.fk_reserve_venue_id=d.id and c.id=b.commodity_type_id and e.id=a.fk_reserve_commodity_sell_id
and datediff(a.update_date,#{startDate})>=0 and datediff(#{endDate},a.update_date)>=0
and d.id=#{reserveVenue.id}
and c.id=#{reserveCommodityType.id}
and b.id=#{reserveCommodity.id}
${sqlMap.dsf} GROUP BY b.id,c.id,d.id

SELECT

FROM reserve_commodity_sell a

WHERE a.id = #{id}

SELECT

FROM reserve_commodity_sell a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}

		and a.id=#{id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM reserve_commodity_sell a


	a.del_flag = #{DEL_FLAG_NORMAL} and tenant_id = #{tenantId}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE reserve_commodity_sell SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
