mapper.select:
SELECT 

FROM reserve_member a

WHERE a.id = #{id}

SELECT

FROM reserve_member a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}

		AND a.sfz =#{sfz}


		AND a.mobile =#{mobile}


		AND a.cartno =#{cartno}


		AND a.cart_type =#{cartType}


		AND a.id !=#{id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
FROM reserve_member a
a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
AND a.cartno LIKE '%'||#{cartno}||'%' '%'+#{cartno}+'%' concat('%',#{cartno},'%')
AND a.cart_type =#{cartType}
${sqlMap.sql}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM reserve_member a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE reserve_member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
