SELECT 

FROM reserve_multiple_payment a


	a.del_flag = #{DEL_FLAG_NORMAL}  and a.tenant_id = #{tenantId}

		and a.order_id=#{orderId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM reserve_multiple_payment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
