SELECT 

FROM reserve_tutor a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.fk_project_id = #{project.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM reserve_tutor a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
