mapper.select:
SELECT

FROM reserve_venue_cons_item a

WHERE a.id = #{id}

SELECT

FROM reserve_venue_cons_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		and DATE(d.cons_date) = DATE (#{consDate})


		and a.venue_id = #{reserveVenue.id}


		and a.cons_data_id = #{consData.id}


		and d.reserve_type != #{consData.reserveType}


ORDER BY a.update_date DESC

SELECT

FROM reserve_venue_cons_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		and Date(a.cons_date) =Date(#{consDate})


		and a.venue_id = #{reserveVenue.id}


		and a.field_id = #{reserveField.id}


		and a.cons_data_id = #{consData.id}


		and d.reserve_type = #{consData.reserveType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM reserve_venue_cons_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		and a.tenant_id = #{tenantId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE reserve_venue_cons_item SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select
from reserve_venue_cons_item a where a.del_flag = #{DEL_FLAG_NORMAL} and tenant_id = #{tenantId}
AND a.cons_mobile LIKE concat(#{consMobile},'%')

SELECT

FROM reserve_venue_cons_item a

WHERE a.cons_mobile like concat('%',#{consMobile},'%') and tenant_id = #{tenantId}

SELECT COUNT( CASE WHEN tmp2.parent_field_id is NULL THEN tmp2.field_id END)+2*COUNT( CASE WHEN parent_field_id is not NULL  THEN tmp2.field_id END)
FROM (
	SELECT DISTINCT(tmp.field_id),r.parent_field_id FROM
	(
		SELECT i.field_id
		FROM reserve_venue_cons_item i
		WHERE i.del_flag = #{DEL_FLAG_NORMAL} AND i.tenant_id = #{tenantId}
		AND STR_TO_DATE(CONCAT_WS(' ',i.cons_date,i.end_time),'%Y-%m-%d %H:%i') >= NOW()
		AND STR_TO_DATE(CONCAT_WS(' ',i.cons_date,i.start_time),'%Y-%m-%d %H:%i') <= NOW()

			AND i.venue_id = #{reserveVenue.id}

		${sqlMap.dsf}
	) tmp
	LEFT JOIN reserve_field_relation r on r.parent_field_id = tmp.field_id
) tmp2
